import React from 'react';
import { Container, Grid, Card, GridRow, Header, Segment } from 'semantic-ui-react';
import EntrepriseAttribute from '../../components/entrepriseAttribute.component';


export function DisplayAction({ controles }) {
    return (
        <Container style={{ padding: '1rem' }}>
            {controles.map(controle => {
                return <Card centered raised fluid key={controle.CONTROLE_IDENT}>
                    <Card.Content>
                        <Grid>
                            <EntrepriseAttribute
                                name="Actions :"
                                icon="building"
                                value={<span>{controle.ACDG_CODE_ACTION}</span>}
                            />

                            <EntrepriseAttribute
                                name="Raison Sociale :"
                                icon="address card"
                                value={<span>{this.state.ETOB_RAISON_SOCIALE}</span>}
                            />
                            <EntrepriseAttribute
                                name="SIRET :"
                                icon="text cursor"
                                value={<span>{this.state.ETOB_SIRET}</span>}
                            />
                            <EntrepriseAttribute
                                name="NAF :"
                                icon="clipboard"
                                value={<span>{this.state.NAF_LIBELLE}</span>}
                            />
                            <EntrepriseAttribute
                                name="Adresse :"
                                icon="address book"
                                value={
                                    <span>
                                        {this.state.ETOB_ADR1} <br />
                                        {this.state.ETOB_ADR2 && <span>{this.state.ETOB_ADR2} <br /></span>}
                                        {this.state.ETOB_ADR3 && <span>{this.state.ETOB_ADR3} <br /></span>}
                                        {this.state.ETOB_ADRCP}
                                        <span> </span>
                                        {this.state.ETOB_ADRVILLE}
                                    </span>
                                }
                            />
                        </Grid>
                    </Card.Content>
                </Card>
            })}

        </Container>
    )
}
