import React from 'react';
import { Grid, List } from 'semantic-ui-react';
import MenuButton from '../../components/menuButton.component';
import moment from 'moment';

export default class VisitesComponent extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            visitesList: [{
                enterprise: "Carrefour",
                date: new Date(),
                id: "19920a3dd"
            },
            {
                enterprise: "Leclerc",
                date: new Date(),
                id: "127226rf3"
            },
            {
                enterprise: "Auchan",
                date: new Date(),
                id: "127236rdz2"
            }]
        }
    }
    render() {
        return (
            <Grid
                textAlign="center"
                style={{ height: '100vh' }}
                verticalAlign="top"
            >
                <Grid.Column style={{ maxWidth: "90vw", margin: "1em" }}>
                    <List divided relaxed>
                        {this.state.visitesList.map(visite => (
                            <List.Item key={visite.id} link={"visite/" + visite.id} color="white"
                                name={<h4>{visite.enterprise}</h4> + moment(visite.date).format("DD/MM/YYYY")} />
                        ))}
                    </List>


                </Grid.Column>
            </Grid >

        );
    }
}
