/* eslint-disable no-unused-vars */
/* eslint-disable no-undef */
import React from 'react';
import _ from 'lodash';
import { Grid, Search, Container, Card, GridRow, Header } from 'semantic-ui-react';
import { Link } from 'react-router-dom';
import { PropTypes } from 'prop-types';
import axios from 'axios';
import config from '../../config';
import {
  changeNameOfPage,
  changeBackUrl,
  changeActivePage
} from '../navbar/actions';
import { connect } from 'react-redux';

const initialState = { isLoading: false, results: [], value: '' };

function mapStateToProps() {
  return {};
}

function mapDispatchToProps(dispatch) {
  return {
    changeNameOfPage: () => dispatch(changeNameOfPage('Ã‰tablissements')),
    changeBackUrl: () => dispatch(changeBackUrl('/menu')),
    changeActivePage: () =>
      dispatch(changeActivePage('etablissements', '/etablissements'))
  };
}

class EntrepriseSearch extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      isLoading: false,
      results: [],
      value: ''
    };
  }

  componentDidMount() {
    this.props.changeNameOfPage();
    this.props.changeBackUrl();
    this.props.changeActivePage();
  }

  handleResultSelect = (e, { result }) =>
    this.setState({ value: result.title });

  handleSearchChange = (e, { value }) => {
    this.setState({ isLoading: true, value });

    this.timeout = setTimeout(async () => {
      if (this.state.value.length < 1) return this.setState(initialState);
      clearTimeout(this.timeout);
      let { data } = await axios.get(config.backend.base_url + '/entreprise/search?query=' + this.state.value)
      this.setState({
        isLoading: false,
        results: data
      });
    }, 400);
  };
  renderResults({ ETOB_IDENT, ETOB_RAISON_SOCIALE, ETOB_ENSEIGNE_LIB, ETOB_SIRET }) {
    return (
      <Container
        fluid
        as={Link}
        to={'/etablissement/' + ETOB_IDENT}
        style={{ color: 'black' }}
      >
        <Grid>
          <Grid.Row>
            <Header>
              {ETOB_ENSEIGNE_LIB || ETOB_RAISON_SOCIALE}
            </Header>
          </Grid.Row>
          <Grid.Row style={{ margin: 0 }}>
            <Grid.Column style={{ fontWeight: 'bold' }} width={7}>
              Enseigne :{' '}
            </Grid.Column>
            <Grid.Column width={8}>{ETOB_ENSEIGNE_LIB}</Grid.Column>
          </Grid.Row>
          <Grid.Row style={{ margin: 0 }}>
            <Grid.Column style={{ fontWeight: 'bold' }} width={7}>
              Raison Sociale :{' '}
            </Grid.Column>
            <Grid.Column width={8}>{ETOB_RAISON_SOCIALE}</Grid.Column>
          </Grid.Row>
          <Grid.Row>
            <Grid.Column style={{ fontWeight: 'bold' }} width={7}>
              Siret :{' '}
            </Grid.Column>
            <Grid.Column width={8}>{ETOB_SIRET}</Grid.Column>
          </Grid.Row>
        </Grid>
      </Container>
    );
  }

  render() {
    const { isLoading, value, results } = this.state;

    return (
      <Container>
        <Grid style={{ marginTop: '1rem' }}>
          <Grid.Column only="tablet computer" width={5} verticalAlign="middle">
            <span style={{ fontWeight: 'bold' }}>Rechercher :</span>
          </Grid.Column>
          <Grid.Column tablet={11} mobile={16} computer={11} textAlign="center">
            <Search
              fluid
              loading={isLoading}
              onResultSelect={this.handleResultSelect}
              onSearchChange={_.debounce(this.handleSearchChange, 500, {
                leading: true
              })}
              value={value}
            />
          </Grid.Column>
        </Grid>
        <Card centered raised fluid>
          <Card.Content>
            <Card.Header textAlign="center">RÃ©sultats</Card.Header>
          </Card.Content>
          <Card.Content>
            <Grid>
              {results.map(result => <GridRow key={result.ETOB_IDENT}>{this.renderResults(result)}</GridRow>)}
            </Grid>
          </Card.Content>
        </Card>
      </Container>
    );
  }
}
EntrepriseSearch.propTypes = {
  changeNameOfPage: PropTypes.func.isRequired,
  changeBackUrl: PropTypes.func.isRequired,
  changeActivePage: PropTypes.func.isRequired
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(EntrepriseSearch);
