import React from 'react';
import logo from './logo.svg';
import './App.css';
import PouchDB from 'pouchdb';
var db = new PouchDB('my_database');
var remoteCouch = 'http://Admin:pass@127.0.0.1:5984/test';
var opts = {live: true};
db.replicate.to(remoteCouch, opts, console.log);
db.replicate.from(remoteCouch, opts, console.log);


export class App extends React.Component {

  constructor(props){
    super(props);
    this.state = {
      text : '',
      user : '',
      list:[]
    }
    db.changes({
      since: 'now',
      live: true
    }).on('change', this.updateTodos.bind(this));
  }

  updateTodos(){
    db.allDocs({include_docs: true, descending: true}, (err, doc) => {
      this.setState({list:doc.rows});
    });
  }

  addTodo(text) {
    var todo = {
      _id: new Date().toISOString(),
      title: text,
      completed: false
    };
    db.put(todo, function callback(err, result) {
      if (!err) {
        console.log('Successfully posted a todo!');
      }
    });
  }

  getTodos(){

  }

  render() {
    return (
      <div>
        <input onChange = {(e)=>this.setState({user:e.target.value})} placeholder="User"/> 
        <input onChange = {(e)=>this.setState({text:e.target.value})} placeholder="Text"/> 
        <button onClick={()=>this.addTodo(this.state.text,this.state.user)}>Ajouter</button>
        <button onClick={()=>this.getTodos()}>refresh</button>
        <div>
          {JSON.stringify(this.state.list)}
        </div>
      </div>
    );
  }
}
