import React from 'react';
import PropTypes from 'prop-types';
import { Grid } from 'semantic-ui-react';
import { Swipeable } from 'react-swipeable';
import './swipeTabs.css';

class SwipeTabsComponent extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            menuIndex: 0
        }
    }
    render() {
        return (
            <Grid textAlign="center"
                style={{ height: '100vh' }}
                verticalAlign="middle">
                <Grid.Row>
                    {this.props.tabs.map((tab, i) => {
                        var className = i == this.state.menuIndex ? "selected-tab-title" : "unselected-tab-title";
                        return <span key={tab.title} className={className} onClick={() => this.setState({ menuIndex: i })}>{tab.title}</span>
                    })}
                </Grid.Row>

                <Grid.Row>
                    <Swipeable onSwipedLeft={() => this.setState({ menuIndex: this.state.menuIndex > 0 ? this.state.menuIndex - 1 : 0 })}
                        onSwipedRight={() => this.setState({ menuIndex: this.state.menuIndex < (this.props.tabs.length - 1) ? this.state.menuIndex + 1 : (this.props.tabs.length - 1) })}>

                        {this.props.tabs[0].component}
                    </Swipeable>
                </Grid.Row>
            </Grid>


        );
    }
}

SwipeTabsComponent.propTypes = {
    tabs: PropTypes.arrayOf(PropTypes.shape({
        title: PropTypes.string,
        component: PropTypes.node
    })).isRequired
};

export default SwipeTabsComponent;
