// reducer
import PouchDBServices from './index';


const initialState = {
  AGENT_DD_IDENT: undefined
};


const postReducer = async (state = initialState, action) => {
  switch (action.type) {
    case 'CHANGE_AGENT': {
      await PouchDBServices.ChangeAgent(action.payload.newAgentIdent)
      return { ...state, AGENT_DD_IDENT: action.payload.newAgentIdent };
    }
    default:
      return state;
  }
};

export default postReducer;
