import React, { Component } from 'react';
import { Grid, Segment, Responsive, Icon, Header } from 'semantic-ui-react';

import { Link } from 'react-router-dom';
import moment from 'moment';

export default class Visite extends Component {
  render() {
    const { visite } = this.props;

    return (
      <Grid.Row style={{ padding: 10, overflow: 'auto' }}>
        <Segment
          style={{ width: '100%' }}
          // to={this.props.link}
          // icon
          // basic
          // fluid
          // className="menubutton"
          // color={this.props.color}
          // size="massive"
        >
          <Link to={'/visite/' + visite.visiteData.VISITE_IDENT}>
            <Grid>
              <Grid.Row verticalAlign="middle" style={{ padding: 0 }}>
                <Grid.Column
                  width={3}
                  style={{
                    backgroundColor: '#f2f2f2',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    height: '100%'
                  }}
                >
                  <Responsive minWidth={600}>
                    <div style={{ display: 'flex', flexDirection: 'row' }}>
                      <Icon name="search" color="grey" size="large"></Icon>
                      <div
                        style={{
                          borderRadius: '50%',

                          width: '20px',
                          height: '20px',

                          background: 'red',
                          color: 'white'
                        }}
                      >
                        {visite.controles.length}
                      </div>
                    </div>
                  </Responsive>
                  <Responsive maxWidth={599}>
                    <div style={{ display: 'flex', flexDirection: 'row' }}>
                      <Icon
                        name="search"
                        color="grey"
                        size="small"
                        style={{ fontSize: '1em' }}
                      ></Icon>
                      <div
                        style={{
                          borderRadius: '50%',

                          width: '20px',
                          height: '20px',

                          background: 'red',
                          color: 'white'
                        }}
                      >
                        {visite.controles.length}
                      </div>
                    </div>
                  </Responsive>
                </Grid.Column>
                <Grid.Column width={7} textAlign="left">
                  <Grid.Row>
                    <Header
                      as="h3"
                      style={{ color: 'grey', overflowWrap: 'break-word' }}
                    >
                      {visite.visiteData.ETOB_RAISON_SOCIALE}
                    </Header>
                  </Grid.Row>
                  <Grid.Row>
                    <Header as="h6" style={{ fontWeight: 'normal' }}>
                      {visite.visiteData.ETOB_NOM_RESPONSABLE}
                    </Header>
                  </Grid.Row>
                </Grid.Column>
                <Grid.Column width={6}>
                  <Grid.Row>
                    <Header as="h6" style={{ fontWeight: 'normal' }}>
                      {/* {this.props.dossier.ACDG_LIBELLE} */}
                    </Header>
                  </Grid.Row>
                  <Grid.Row>
                    <Responsive minWidth={600}>
                      <Header as="h4" style={{ overflowWrap: 'break-word' }}>
                        {visite.visiteData.ETOB_ADR1}{' '}
                        {visite.visiteData.ETOB_ADR2}{' '}
                        {visite.visiteData.ETOB_ADR3} -{' '}
                        {visite.visiteData.ETOB_ADRVILLE}
                      </Header>
                    </Responsive>
                    <Responsive
                      maxWidth={600}
                      style={{ overflowWrap: 'break-word' }}
                    >
                      <Header as="h5">
                        {visite.visiteData.ETOB_ADR1}{' '}
                        {visite.visiteData.ETOB_ADR2}{' '}
                        {visite.visiteData.ETOB_ADR3} -{' '}
                        {visite.visiteData.ETOB_ADRVILLE}
                      </Header>
                    </Responsive>
                  </Grid.Row>
                  <Grid.Row>
                    <Header
                      as="h4"
                      style={{ fontWeight: 'normal', fontStyle: 'italic' }}
                    >
                      {moment(
                        visite.visiteData.VISITE_DATE_INTERVENTION
                      ).format('DD/MM/YYYY')}
                    </Header>
                  </Grid.Row>
                </Grid.Column>
              </Grid.Row>
            </Grid>
          </Link>
        </Segment>
      </Grid.Row>
    );
  }
}
