import React from 'react';
import { Container, Grid, Card, GridRow, Header, Segment } from 'semantic-ui-react';
import EntrepriseAttribute from '../../components/entrepriseAttribute.component';


export function DisplayAction({ controles }) {
    return (
        <Container style={{ padding: '1rem' }}>
            {controles.map(controle => {
                return <Card centered raised fluid key={controle.CONTROLE_IDENT}>
                    <Card.Content>
                        <Grid>
                            <EntrepriseAttribute
                                name="Action :"
                                icon="building"
                                style={{ padding: "0" }}
                                value={<span>{controle.ACDG_CODE_ACTION}{controle.ACDG_LIBELLE}</span>}
                            />
                            <EntrepriseAttribute
                                name="Produit :"
                                icon="building"
                                value={<span>{controle.CPF_CODE_PRODUIT}{controle.CPF_LIBELLE}</span>}
                            />

                        </Grid>
                    </Card.Content>
                </Card>
            })}

        </Container>
    )
}
